mapper.select:
select id, user_name, nicK_name, avatar, email, phonenumber, sex, tabs, way, way_str, wx_card, zx_word, zx_style, notice, experience, info, detail, serve, work_num, work_time, work_hours, del_flag, is_show, status, qualification, index_qualification, create_by, create_time, update_by, update_time,
qualification, index_qualification, mode, city, province, genre, img, share, lang,
(SELECT count(id) FROM psy_consult_work WHERE `live` != '[]' and `live` != `used` and consult_id = psy_consult.id and `day` = DATE_FORMAT(NOW(),'%Y-%m-%d')) buy,
(SELECT b.price FROM psy_consult_serve a INNER JOIN psy_consult_server_config b ON a.serve_id=b.id AND a.consult_id=psy_consult.id WHERE b.bound = 0 ORDER BY b.price ASC LIMIT 1) price,
(SELECT b.price FROM psy_consult_serve a INNER JOIN psy_consult_server_config b ON a.serve_id=b.id AND a.consult_id=psy_consult.id WHERE b.bound = 1 ORDER BY b.price ASC LIMIT 1) new_price
from psy_consult

    and status = 0 and del_flag = 0 and is_show = 0
     and user_name like concat('%', #{userName}, '%')
     and sex = #{sex}
     and city = #{city}
     and province = #{province}



                and (SELECT count(id) FROM psy_consult_work WHERE `live` != '[]' and `live` != `used` and consult_id = psy_consult.id and `day` = DATE_FORMAT(NOW(),'%Y-%m-%d')) > 0

            and id in (
            SELECT a.consult_id FROM psy_consult_serve a INNER JOIN psy_consult_server_config b on a.serve_id = b.id WHERE a.consult_id = psy_consult.id
             and b.type = 1
             and b.id = #{serveId}

                and b.price >= #{lowPrice} and b.price <= #{highPrice}

            GROUP BY a.consult_id
            )


            and (

                    id in (
                    SELECT a.consult_id FROM psy_consult_serve a INNER JOIN psy_consult_server_config b on a.serve_id = b.id and a.consult_id = psy_consult.id

                         or b.type = 1
                         or b.id = #{serveId}

                            or b.price >= #{lowPrice}


                            or b.price <= #{highPrice}


                    GROUP BY a.consult_id
                    )

                        or



                    (SELECT count(id) FROM psy_consult_work WHERE `live` != '[]' and `live` != `used` and consult_id = psy_consult.id and `day` = DATE_FORMAT(NOW(),'%Y-%m-%d')) > 0

            )



                and id in (
                SELECT a.consult_id FROM psy_consult_serve a INNER JOIN psy_consult_server_config b on a.serve_id = b.id WHERE a.consult_id = psy_consult.id
                 and b.`mode` = #{serve}

                    and b.price >= #{lowPrice} and b.price <= #{highPrice}

                GROUP BY a.consult_id
                )




        and id in (
            SELECT consult_id FROM psy_consult_work WHERE `status` = 0 and consult_id = psy_consult.id


                    and `time_start` >= '06:00' and `time_end` <= '12:00'


                    and `time_start` >= '12:00' and `time_end` <= '18:00'


                    and `time_start` >= '18:00' and `time_end` <= '24:00'




                and `day` in

                    #{item,jdbcType=VARCHAR}


            GROUP BY consult_id
        )


order by IF(isnull(new_price),1,0), new_price asc, IF(isnull(price),1,0), price asc, id asc
