mapper.select:
select cf.id, cf.mode, cf.type, cf.name, cf.info, cf.price, cf.time, cf.num,cf.consultant_ratio,
    (SELECT COUNT(id) from psy_consult_order WHERE
    serve_id = cf.id and pay_status = '2'

            and consult_id = #{cId}

    ) sales,
    (SELECT COUNT(consult_id) from psy_consult_serve WHERE serve_id = cf.id) ref,
    cf.z_index, cf.bound, cf.end, cf.del_flag, cf.status, cf.create_by, cf.create_time, cf.update_by, cf.update_time,


       re.relation_id as relationId,

    cf.level ,cf.service_object
from psy_consult_server_config cf

        left join psy_consult_serve re on re.serve_id = cf.id and re.consult_id = #{cId}


     and cf.name like concat('%', #{name}, '%')
     and cf.status = #{status}
     and cf.type = #{type}
     and cf.del_flag = #{delFlag}
     and cf.create_time  >=  #{startTime}
     and cf.create_time  <=  #{endTime}

        and cf.id in (select distinct serve_id from psy_consult_serve where consult_id = #{cId})


        and cf.id not in (select distinct serve_id from psy_consult_serve where consult_id = #{nId})


        and level = #{level}


        and  #{serviceObject} like concat('%',service_object,'%')



order by cf.z_index asc
