mapper.select:
select 
   sc.id, sc.order_id,sc.team_id, sc.work_id,sc.time_num,sc.schedule_type, sc.time_start, sc.time_end, sc.day, sc.week, sc.status, sc.create_by,
   sc.create_time, sc.update_by, sc.update_time, sc.time, sc.real_time, sc.consult_id ,
    COALESCE(team.title, cf.name) AS serverName,  ifnull(con.nick_name,'') as userNickName ,ifnull(con.id,'') as userId, ifnull(cf.mode,'') as mode
from psy_consultant_schedule sc
    left join psy_consultant_team_supervision team on team.id = sc.team_id
    left join psy_consult con on con.id = sc.create_by   
    left join psy_consultant_order orde on orde.order_no = sc.order_id
    left join psy_consult_serve ser on ser.relation_id = orde.server_id 
    left join psy_consult_server_config cf on ser.serve_id = cf.id

     and sc.order_id = #{orderId}
     and sc.team_id = #{teamId}
     and sc.work_id = #{workId}
     and sc.schedule_type = #{scheduleType}
     and sc.time_start = #{timeStart}
     and sc.time_end = #{timeEnd}
     and sc.day = #{day}
     AND sc.day >= #{start}
     AND sc.day <= #{end}
     and sc.week = #{week}
     and sc.status = #{status}
     and sc.time = #{time}
     and sc.real_time = #{realTime}
     and sc.create_by = #{createBy}
     and sc.consult_id = #{consultId}
     and sc.customer_confirm = #{customerConfirm}
     and sc.work_record = #{workRecord}
     and sc.real_time >= #{realTimeStart}

        and sc.status in

            #{item}




    order by ${orderBy} ${orderDir}

mapper.update:
update psy_consultant_schedule
order_id = #{orderId}, work_id = #{workId}, team_id = #{teamId}, time_num = #{timeNum}, schedule_type = #{scheduleType}, time_start = #{timeStart}, time_end = #{timeEnd}, day = #{day}, week = #{week}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, time = #{time}, real_time = #{realTime}, consult_id = #{consultId}, customer_confirm = #{customerConfirm}, work_record = #{workRecord},
where id = #{id}

mapper.select:
select
    count(sc.id)
from
    psy_consultant_schedule sc
where 1=1

    and sc.consult_id =  #{chargeConsultId}


    and sc.create_by = #{payConsultId}


    and sc.order_id = #{orderNo}

    and sc.real_time <= #{realTime}
