mapper.select:
select
    tem.id, tem.user_type, tem.coupon_name, tem.total_num,  tem.coupon_type, tem.server_type, tem.max_deduction_price, tem.discount_rate, tem.validity_day,
    tem.template_status,  tem.create_by, tem.create_time, tem.update_by, tem.update_time, tem.use_threshold_price, tem.remark, tem.is_free_get,
       count(cou.coupon_no) as used_num
from psy_coupon_template tem
left join psy_coupon cou on tem.id = cou.template_id



    del_flag = 0
     and tem.user_type = #{userType}
     and tem.coupon_name like concat('%', #{couponName}, '%')
     and tem.total_num = #{totalNum}
     and tem.used_num = #{usedNum}
     and tem.coupon_type = #{couponType}
     and tem.server_type = #{serverType}
     and tem.max_deduction_price = #{maxDeductionPrice}
     and tem.discount_rate = #{discountRate}
     and tem.validity_day = #{validityDay}
     and tem.template_status = #{templateStatus}
     and tem.use_threshold_price = #{useThresholdPrice}
     and tem.is_free_get = #{isFreeGet}


group by tem.id

mapper.update:
update psy_coupon_template
user_type = #{userType}, coupon_name = #{couponName}, total_num = #{totalNum}, used_num = #{usedNum}, coupon_type = #{couponType}, server_type = #{serverType}, max_deduction_price = #{maxDeductionPrice}, discount_rate = #{discountRate}, validity_day = #{validityDay}, template_status = #{templateStatus}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, use_threshold_price = #{useThresholdPrice}, remark = #{remark}, is_free_get = #{isFreeGet},
where id = #{id}
